fd -p 'websites/ethernodes.org/\d+/index.html' | xargs -I{} bash -c 'FILENAME="{}"; echo $FILENAME"	"$(htmlq ".container script[type]:last-child" --text -f $FILENAME | head -n 1 | cut -c20- | jq -c "map({key: .name, value: .y}) | from_entries")' | jq -R 'split("\t") | select(.[1] != "") | {key: .[0] | split("/")[3], value: .[1] | fromjson}' | jq -s 'from_entries' > 'network_2.json'
